import { createContext, useState, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('token'); 
      if (token) {
        try {
          // إرسال الـ token في الـ headers للتحقق من صلاحية الجلسة
          const res = await axios.get('/api/auth/check', {
            headers: {
              Authorization: `Bearer ${token}`,  
            },
          });
          setUser(res.data.user);  // إذا كان الـ token صحيحاً
        } catch (err) {
          setUser(null);  // إذا كانت هناك مشكلة في التحقق
        }
      } else {
        setUser(null);  // إذا لم يكن هناك token
      }
      setLoading(false);  // إغلاق حالة التحميل
    };
    checkAuth();
  }, []);

  const login = async (credentials) => {
    try {
      const res = await axios.post('/api/auth/login', credentials);
      const { token, user } = res.data;

      // تخزين الـ token في localStorage
      localStorage.setItem('token', token);
      
      setUser(user); 
      return user; 
    } catch (error) {
      throw error; 
    }
  };

  const register = async (userData) => {
    const res = await axios.post('/api/auth/register', userData);
    return res.data;
  };

  const logout = async () => {
    await axios.post('/api/auth/logout');
    
    localStorage.removeItem('token');
    
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, loading, login, register, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
