import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from '../../services/api';
import PropertyCard from '../../components/PropertyCard';

const GuestProperties = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        const res = await axios.get('/api/properties');
        setProperties(res.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load properties');
      } finally {
        setLoading(false);
      }
    };
    
    fetchProperties();
  }, []);

  const handleAction = (action) => {
    if (action === 'save' || action === 'comment' || action === 'apply') {
      navigate('/login', { state: { message: 'Please login to perform this action' } });
    }
  };

  if (loading) return <div>Loading properties...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="guest-properties">
      <div className="guest-banner">
        <h2>You are browsing as a guest</h2>
        <p>Some features are limited. <button onClick={() => navigate('/register')}>Register</button> or <button onClick={() => navigate('/login')}>Login</button> for full access.</p>
      </div>
      
      <div className="properties-grid">
        {properties.map(property => (
          <PropertyCard 
            key={property._id} 
            property={property}
            onAction={handleAction}
            isGuest={true}
          />
        ))}
      </div>
    </div>
  );
};

export default GuestProperties;
