import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from '../services/api';
import FeaturedProperties from '../components/FeaturedProperties';
import '../styles/Home.css';
import '../styles/Navbar.css';
i

const Home = () => {
  const [featuredProperties, setFeaturedProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchFeaturedProperties = async () => {
      try {
        const response = await axios.get('/api/properties/featured');
        setFeaturedProperties(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load featured properties');
      } finally {
        setLoading(false);
      }
    };

    fetchFeaturedProperties();
  }, []);

  return (
    <div className="home-page">
      <section className="hero-section">
        <div className="hero-content">
          <h1>Find Your Perfect Rental Home</h1>
          <p>Discover the best properties for rent in your desired location</p>
          <Link to="/properties" className="btn-primary">
            Browse Properties
          </Link>
        </div>
      </section>

      <section className="featured-properties">
        <div className="container">
          <h2>Featured Properties</h2>
          {loading ? (
            <div>Loading featured properties...</div>
          ) : error ? (
            <div className="error">{error}</div>
          ) : (
            <>
              <FeaturedProperties properties={featuredProperties} />
              <div className="text-center">
                <Link to="/properties" className="btn-secondary">
                  View All Properties
                </Link>
              </div>
            </>
          )}
        </div>
      </section>

      {/*  التزويد بعدين بقا */}
    </div>
  );
};

export default Home;
