import React, { useState, useEffect } from "react";
import "../styles/ho.css";

const Home = () => {
  const [properties, setProperties] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [likes, setLikes] = useState([]);

  // استدعاء API لتحميل بيانات العقارات
  useEffect(() => {
    // في حالتك يمكنك استخدام API خاص بك، مثل:
    fetch("https://api.example.com/properties")
      .then((response) => response.json())
      .then((data) => {
        setProperties(data);
      })
      .catch((error) => {
        console.error("Error fetching properties:", error);
      });
  }, []);

  // التعامل مع إضافة العقار للمفضلة
  const toggleFavorite = (id) => {
    setFavorites((prevFavorites) => {
      if (prevFavorites.includes(id)) {
        return prevFavorites.filter((propertyId) => propertyId !== id);
      } else {
        return [...prevFavorites, id];
      }
    });
  };

  // التعامل مع إعجاب العقار
  const toggleLike = (id) => {
    setLikes((prevLikes) => {
      if (prevLikes.includes(id)) {
        return prevLikes.filter((propertyId) => propertyId !== id);
      } else {
        return [...prevLikes, id];
      }
    });
  };

  return (
    <div className="home-container">
      <h2>Available Properties</h2>
      <div className="properties-list">
        {properties.map((property) => (
          <div key={property.id} className="property-card">
            <img src={property.image} alt={property.title} className="property-image" />
            <h3>{property.title}</h3>
            <p>{property.description}</p>
            <p><strong>Price: ${property.price}</strong></p>
            <p><strong>Location: {property.location}</strong></p>

            {/* المفضلة */}
            <button
              onClick={() => toggleFavorite(property.id)}
              className={`favorite-btn ${favorites.includes(property.id) ? "favorited" : ""}`}
            >
              {favorites.includes(property.id) ? "Remove from Favorites" : "Add to Favorites"}
            </button>

            {/* إعجاب */}
            <button
              onClick={() => toggleLike(property.id)}
              className={`like-btn ${likes.includes(property.id) ? "liked" : ""}`}
            >
              {likes.includes(property.id) ? "Unlike" : "Like"}
            </button>

            {/* التعليقات */}
            <div className="comments-section">
              <h4>Comments:</h4>
              <ul>
                {property.comments.map((comment, index) => (
                  <li key={index}>
                    <strong>{comment.user}: </strong>{comment.comment}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
