import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Home.css";

const LoginForm = ({ onSuccess }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });
      const data = await res.json();

      if (res.ok) {
        if (data.role === "landlord" && data.status !== "approved") {
          setError("Your account is pending admin approval.");
          return;
        }

        localStorage.setItem("token", data.token);
        localStorage.setItem("role", data.role);
        localStorage.setItem("userId", data.userId);
        onSuccess?.();
      } else {
        setError(data.message || "Invalid login credentials.");
      }
    } catch {
      setError("An error occurred during login.");
    }
  };

  return (
    <form onSubmit={handleLogin}>
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
      {error && <p className="error">{error}</p>}
      <p className="note">* Landlords must be approved by admin before logging in.</p>
      <button type="submit" className="btn login-btn">Login</button>
    </form>
  );
};

const RegisterForm = () => {
  const [role, setRole] = useState("tenant");
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: "",
    ownershipProof: null,
  });
  const [error, setError] = useState("");
  const [successMsg, setSuccessMsg] = useState("");

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  const handleRegister = async (e) => {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      return setError("Passwords do not match.");
    }

    const data = new FormData();
    data.append("role", role);
    data.append("username", formData.username);
    data.append("email", formData.email);
    data.append("phone", formData.phone);
    data.append("password", formData.password);
    if (role === "landlord" && formData.ownershipProof) {
      data.append("ownershipProof", formData.ownershipProof);
    }

    try {
      const res = await fetch("http://localhost:5000/api/register", {
        method: "POST",
        body: data,
      });
      const result = await res.json();

      if (res.ok) {
        setSuccessMsg(
          role === "landlord"
            ? "Registered successfully. Please wait for admin approval."
            : "Registered successfully. You can now login."
        );
        setError("");
      } else {
        setError(result.message || "Registration failed.");
      }
    } catch {
      setError("An error occurred during registration.");
    }
  };

  return (
    <form onSubmit={handleRegister}>
      <select value={role} onChange={(e) => setRole(e.target.value)}>
        <option value="tenant">Tenant</option>
        <option value="landlord">Landlord</option>
      </select>

      <input type="text" name="username" placeholder="Username" onChange={handleChange} required />
      <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
      <input type="tel" name="phone" placeholder="Phone Number" onChange={handleChange} required />
      <input type="password" name="password" placeholder="Password" onChange={handleChange} required />
      <input type="password" name="confirmPassword" placeholder="Confirm Password" onChange={handleChange} required />

      {role === "landlord" && (
        <div className="upload">
          <label>Ownership Proof:</label>
          <input type="file" name="ownershipProof" accept="image/*,.pdf" onChange={handleChange} required />
        </div>
      )}

      {error && <p className="error">{error}</p>}
      {successMsg && <p className="success">{successMsg}</p>}

      <button type="submit" className="btn register-btn">Register</button>
    </form>
  );
};

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const navigate = useNavigate();

  const handleGuestAccess = () => {
    localStorage.setItem("role", "guest");
    navigate("/home");
  };

  return (
    <div className="auth-container">
      <div className="auth-box">
        <div className="tabs">
          <button className={isLogin ? "active" : ""} onClick={() => setIsLogin(true)}>Login</button>
          <button className={!isLogin ? "active" : ""} onClick={() => setIsLogin(false)}>Register</button>
        </div>

        {isLogin ? <LoginForm onSuccess={() => navigate("/home")} /> : <RegisterForm />}

        <div className="guest">
          <button onClick={handleGuestAccess}>Continue as Guest ðŸ‘€</button>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
