import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const ProductDetails = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`https://fakestoreapi.com/products/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setProduct(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching product:", error);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <div style={{ padding: "20px" }}>جاري التحميل...</div>;
  if (!product) return <div style={{ padding: "20px" }}>المنتج غير موجود.</div>;

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "0 auto" }}>
      <h2>{product.title}</h2>
      <img src={product.image} alt={product.title} style={{ width: "100%", maxWidth: "300px" }} />
      <p><strong>السعر:</strong> ${product.price}</p>
      <p><strong>الوصف:</strong> {product.description}</p>
      <p><strong>الفئة:</strong> {product.category}</p>
    </div>
  );
};

export default ProductDetails;
