import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const PropertyDetails = () => {
  const { id } = useParams();
  const [property, setProperty] = useState(null);

  useEffect(() => {
    fetch(`https://fakestoreapi.com/products/${id}`)
      .then((res) => res.json())
      .then((data) =>
        setProperty({
          ...data,
          location: "Unknown Location",
          comments: [
            { user: "John", comment: "Nice property!" },
            { user: "Alice", comment: "Great deal." },
          ],
        })
      )
      .catch((err) => console.error("Error fetching property:", err));
  }, [id]);

  if (!property) return <p>Loading...</p>;

  return (
    <div className="property-details-container">
      <h2>{property.title}</h2>
      <img src={property.image} alt={property.title} className="property-image" />
      <p>{property.description}</p>
      <p><strong>Price: ${property.price}</strong></p>
      <p><strong>Location: {property.location}</strong></p>

      <h4>Comments:</h4>
      <ul>
        {property.comments.map((c, i) => (
          <li key={i}><strong>{c.user}:</strong> {c.comment}</li>
        ))}
      </ul>
    </div>
  );
};

export default PropertyDetails;
