import React, { useState, useEffect } from "react"; 
import { Routes, Route, Navigate, Outlet, useLocation } from "react-router-dom";
import Navbar from "./components/Navbar";
import Home from "./pages/Home";
import ProductDetails from "./pages/ProductDetails";
import Cart from "./pages/Cart";
import Login from "./pages/Login";
import Wishlist from "./pages/Wishlist";
import Profile from "./pages/Profile";
import Checkout from "./pages/Checkout";
import Categories from "./pages/Categories";
import About from "./pages/About";
import Contact from "./pages/Contact";

const ProtectedRoute = () => {
  const user = JSON.parse(localStorage.getItem("user"));
  return user ? <Outlet /> : <Navigate to="/login" replace />;
};

const App = () => {
  const [cart, setCart] = useState([]);
  const location = useLocation(); 
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("user")) || { role: "guest" });

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart"));
    if (storedCart) setCart(storedCart);
  }, []);

  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  useEffect(() => {
    // في حال تسجيل الدخول بنجاح، نقوم بتحديث حالة المستخدم
    if (user.role !== "guest") {
      // التوجيه إلى الصفحة المناسبة بناءً على الدور
      if (user.role === "landlord") {
        // يمكن إضافة التوجيه إلى صفحة معينة بعد تسجيل الدخول كـ مالك
        window.location.href = "/properties"; // أو صفحة أخرى تناسب المالك
      } else if (user.role === "tenant") {
        window.location.href = "/profile"; // أو صفحة أخرى تناسب المستأجر
      } else if (user.role === "admin") {
        window.location.href = "/admin-dashboard"; // أو صفحة أخرى تناسب الأدمن
      }
    }
  }, [user]);

  return (
    <>
      {location.pathname !== "/login" && location.pathname !== "/register" && <Navbar user={user} cart={cart} />}

      <Routes>
        <Route path="/login" element={<Login setUser={setUser} />} />
        <Route element={<ProtectedRoute />}>
          <Route path="/" element={<Home />} />
          <Route path="/product/:id" element={<ProductDetails />} />
          <Route path="/cart" element={<Cart cart={cart} setCart={setCart} />} />
          <Route path="/wishlist" element={<Wishlist />} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/checkout" element={<Checkout />} />
          <Route path="/categories" element={<Categories cart={cart} setCart={setCart} />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
        </Route>
      </Routes>
    </>
  );
};

export default App;
