import React, { useEffect, useState } from "react";
import axios from "axios";
import ProductCard from "../components/ProductCard";
import Navbar from "../components/Navbar";
import Hero from "../components/Hero";
import Footer from "../components/Footer";
import "../styles/Home.css";

const Home = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    axios.get("https://fakestoreapi.com/products")
      .then(response => {
        setProducts(response.data);
        setFilteredProducts(response.data);
      })
      .catch(error => console.log(error));
  }, []);

  const handleSearch = (query) => {
    setSearchQuery(query);
    const filtered = products.filter(product =>
      product.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredProducts(filtered);
  };

  return (
    <div className="home-container">
      <Navbar onSearch={handleSearch} />
      <Hero />

      {/* 🔍 شريط البحث */}
      <div className="search-container">
        <input
          type="text"
          className="search-bar"
          placeholder="🔍 ابحث عن المنتجات..."
          value={searchQuery}
          onChange={(e) => handleSearch(e.target.value)}
        />
      </div>

      {/* 🎯 بانر متحرك - Slider */}
      <section className="slider">
        <img src="https://source.unsplash.com/1200x400/?shopping,discount" alt="عرض خاص" />
        <div className="slider-text">
          <h2>🔥 خصومات تصل إلى 70% على جميع المنتجات!</h2>
          <button>تسوق الآن</button>
        </div>
      </section>

      {/* 🏆 قسم "أفضل المبيعات" */}
      <section className="best-sellers">
        <h2>🏆 الأكثر مبيعًا</h2>
        <div className="best-sellers-grid">
          <img src="https://source.unsplash.com/200x200/?shoes" alt="أحذية" />
          <img src="https://source.unsplash.com/200x200/?watches" alt="ساعات" />
          <img src="https://source.unsplash.com/200x200/?electronics" alt="إلكترونيات" />
          <img src="https://source.unsplash.com/200x200/?fashion" alt="ملابس" />
        </div>
      </section>

      {/* ⭐ قسم "لماذا نحن؟" */}
      <section className="why-us">
        <h2>لماذا تختارنا؟ 🤔</h2>
        <div className="why-us-grid">
          <div>
            <h3>🚚 شحن سريع</h3>
            <p>نوفر خدمة الشحن السريع لجميع أنحاء الوطن العربي.</p>
          </div>
          <div>
            <h3>🔒 دفع آمن</h3>
            <p>نوفر لك خيارات دفع آمنة 100% مع تشفير عالي.</p>
          </div>
          <div>
            <h3>💰 أسعار تنافسية</h3>
            <p>أفضل العروض والخصومات على مختلف المنتجات.</p>
          </div>
          <div>
            <h3>⭐ ضمان الجودة</h3>
            <p>جميع المنتجات أصلية ومضمونة من الشركات المصنعة.</p>
          </div>
        </div>
      </section>

      {/* 🛍️ المنتجات */}
      <h1 className="home-title">🛍 جميع المنتجات</h1>
      <div className="product-grid">
        {filteredProducts.length > 0 ? (
          filteredProducts.map(product => (
            <ProductCard key={product.id} product={product} />
          ))
        ) : (
          <p className="no-products">❌ لا توجد منتجات مطابقة لبحثك</p>
        )}
      </div>

      {/* 📝 قسم المدونة */}
      <section className="blog">
        <h2>📝 أحدث المقالات</h2>
        <div className="blog-grid">
          <div className="blog-card">
            <img src="https://source.unsplash.com/300x200/?shopping" alt="نصائح التسوق" />
            <h3>📢 كيف تحصل على أفضل العروض؟</h3>
            <p>اكتشف أسرار الشراء الذكي وتوفير المال...</p>
          </div>
          <div className="blog-card">
            <img src="https://source.unsplash.com/300x200/?fashion" alt="موضة 2024" />
            <h3>🔥 أحدث صيحات الموضة 2024</h3>
            <p>تعرف على الأزياء الرائجة لهذا الموسم...</p>
          </div>
        </div>
      </section>

      {/* 💬 آراء العملاء */}
      <section className="reviews">
        <h2>💬 ماذا يقول عملاؤنا؟</h2>
        <div className="reviews-grid">
          <div className="review-card">
            <p>"متجر رائع وخدمة مميزة! ⭐⭐⭐⭐⭐"</p>
            <span>- سارة، جدة</span>
          </div>
          <div className="review-card">
            <p>"أفضل تجربة تسوق أونلاين! 🚀"</p>
            <span>- محمد، دبي</span>
          </div>
          <div className="review-card">
            <p>"توصيل سريع وأسعار منافسة! ❤️"</p>
            <span>- ليلى، القاهرة</span>
          </div>
        </div>
      </section>

      {/* 🏁 الفوتر */}
      <Footer />
    </div>
  );
};

export default Home;
