import React from "react";
import { Link } from "react-router-dom";
import "../styles/Footer.css";

const Footer = () => {
  return (
    <footer className="footer">
      <div className="footer-container">
        
        {/* 🏪 القسم الأول: معلومات المتجر */}
        <div>
          <h2>🛍️ Shopwise</h2>
          <p>أفضل متجر إلكتروني لشراء المنتجات عالية الجودة بأسعار رائعة.</p>
        </div>

        {/* 🔗 القسم الثاني: الروابط السريعة */}
        <div>
          <h3>روابط سريعة</h3>
          <ul className="footer-links">
            <li><Link to="/">🏠 الصفحة الرئيسية</Link></li>
            <li><Link to="/categories">📂 التصنيفات</Link></li>
            <li><Link to="/about">ℹ️ من نحن</Link></li>
            <li><Link to="/contact">📞 تواصل معنا</Link></li>
          </ul>
        </div>

        {/* 🌐 القسم الثالث: وسائل التواصل */}
        <div>
          <h3>تواصل معنا</h3>
          <p>📍 العنوان: شارع التجارة، الرياض</p>
          <p>📧 البريد: support@shopwise.com</p>
          <p>📞 الهاتف: 920012345</p>
          <div className="social-links">
            <a href="#">🌍 فيسبوك</a>
            <a href="#">🐦 تويتر</a>
            <a href="#">📷 إنستجرام</a>
          </div>
        </div>

      </div>

      {/* ⓒ حقوق النشر */}
      <div className="footer-bottom">
        &copy; {new Date().getFullYear()} Shopwise. جميع الحقوق محفوظة.
      </div>

      {/* 🖼️ صور العروض الترويجية */}
      <div className="promo-images">
        <img src="https://source.unsplash.com/200x100/?fashion,discount" alt="Promo 1" />
        <img src="https://source.unsplash.com/200x100/?electronics,sale" alt="Promo 2" />
        <img src="https://source.unsplash.com/200x100/?shoes,offer" alt="Promo 3" />
      </div>

    </footer>
  );
};

export default Footer;
